package basics.alphabetspattern;

public class ReverseAlphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=1; i<=7; i++) {
    for(int j=1; j<=7; j++) {
    	System.out.println(i+"  ");
    	System.out.println(j+"  ");
    }
   
    }
    System.out.println();
	}

}
